import React, {Component} from 'react';
import './Counter.css';

class Licznik extends Component {

    constructor(props) {
        super(props);

        this.state = {
            wartośćLicznika: this.props.atrybut
        }
    }

    addValue = () => {
        this.setState((poprzedniState) => {
            return({
                wartośćLicznika: poprzedniState.wartośćLicznika + 1
            })
        })
    }

    render() {
        return (
            <div className='licznik'>
                {this.state.wartośćLicznika}
                <button onClick={this.addValue}>Add 1</button>
            </div>
        )
    }
}

