import './Display.css';

const Display = (props) => {

    return (
        <div>
            <div className={`licznik ${(props.wartośćDisplay <= 5) ? 'blue' : 'red'}`}>
                {props.wartośćDisplay}
            </div>
        </div>
    )
}

export default Display;
