import React from 'react';
import './Display.css';

const Display = (props) => {
    let classNameValue = 'blue';
    if(props.displayValue <= 5) {
        classNameValue = 'blue';
    } else {
        classNameValue = 'red';
    }

    document.querySelector('.add').addEventListener('click', () => {
        props.buttonMethod;
    })

    return (
        <div>
            <div className={`licznik ${classNameValue}`}>
                {props.displayValue}
                <button className='add'>AddButton</button>
            </div>
        </div>
    )
}

export default Display;
