import React from 'react';
import './ButtonsPanel.css'

const ChangeColorButtons = (props) => {

    // document.getElementById('change-buttons').querySelectorAll('button').forEach(button => {
    //     button.addEventListener('click', (e) => {
    //         e.target.classList='';
    //         e.target.classList.add(props.method)
    //     });
    // });

    ChangeClass = (color) => {

        if (color === 'blue') {
            return 'blue';
        } else if (color === 'yellow') {
            return 'yellow';
        } else {
            return 'green';
        };
    }

    return(
        <div id='change-buttons'>
            <button onClick={ (e) => {
                e.target.classList.toggle('blue');
            }}>Blue</button>
            <button onClick={ (e) => {
                e.target.classList.toggle('yellow');
            }}>yellow</button>
            <button onClick={ (e) => {
                e.target.classList.toggle('green');
            }}>green</button>
        </div>
    );
}

export default ChangeColorButtons;
