import React, {Component} from 'react';

class Step extends Component {

    constructor (props) {
        super(props);
        this.setStep = React.createRef();
    }

    render() {
        return(
            <div>
                <label for='step'>Krok:</label>
                <input ref={this.inputSetStep} type='number' id='step' placeholder='set step'></input>
            </div> 
        )
    }
}

export default Step;
