import React, {Component} from 'react';

class ChangeColor extends Component{

    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div>
                <div id='change-buttons'>
                    <h1>HELLO</h1>

                    <button onClick={ (e) => {
                        e.target.claglssList.toge('blue');
                    }}>Blue</button>
                    <button onClick={ (e) => {
                        e.target.classList.toggle('yellow');
                    }}>yellow</button>
                    <button onClick={ (e) => {
                        e.target.classList.toggle('green');
                    }}>green</button>
                </div>
            </div>
        )
    }
}

export default ChangeColor;
