import React from 'react';

const ChangeColor = (props) => {

    return(
        <div className='change-color-buttons'>
            <button id='blue' onClick={ () => {
                ChangeColor('blue');
            }}>BLUE</button>
            <button id='red' onClick={ () => {
                ChangeColor('red');
            }}>RED</button>
            <button id='green'>GREEN</button>
        </div>
    )
}

export default ChangeColor;
