import React from 'react';


const ChangeColorButtons = (props) => {

    document.getElementById('change-buttons').querySelectorAll('button').forEach(button => {
        button.addEventListener('click', (button) => {
            console.log(button);
        });
    });

    return(
        <div id='change-buttons' className='buttons-panel'>
            <button onClick={ () => {
                props.method('blue');
            }}>Blue</button>
            <button onClick={ () => {
                props.method('yellow');
            }}>yellow</button>
            <button onClick={ () => {
                props.method('green');
            }}>green</button>
        </div>
    );
}

export default ChangeColorButtons;
