import React, {Component} from 'react';
import './Counter.css';

class Licznik extends Component {

    constructor(props) {
        super(props);

        this.state = {
            wartośćLicznika: this.props.atrybut
        }
    }
    render() {
        return (
            <div className='licznik'>
                {this.props.atrybut}
                <button>Add 1</button>
            </div>
        )
    }
}

