import React from 'react';
import './Display.css';

const Display = (props) => {
let classNameValue = 'blue';
if(props.wartośćDisplay <= 5) {
    classNameValue = 'blue';
} else {
    classNameValue = 'red';
}
    return (
        <div>
            <div className={`licznik ${classNameValue}`}>
                {props.wartośćDisplay}
            </div>
        </div>
    )
}

export default Display;
