import React, {Component} from 'react';
import ChangeColorButtons from './ChangeColorButtons';

class ChangeColor extends Component{

    constructor(props) {
        super(props);
    }

    ChangeClass = (color) => {

        if (color === 'blue') {
            console.log('blue');
        } else if (color === 'yellow') {
            console.log('yellow');
        } else {
            console.log('green');
        };
    }

    render() {
        return(
            <div>
                <ChangeColorButtons method={this.ChangeClass}/>
            </div>
        )
    }
}

export default ChangeColor;
